and t1.businessdate >= #{condition.datefrom }
and t1.businessdate <= #{condition.dateto }
and t1.personid = #{condition.personid }
and t1.userid in
#{item }
and find_in_set(t3.departmentid, #{condition.deptid })
order by businessdate desc, personid, userid

