mapper.update:
UPDATE t_base_logistics_line_customer t
  set t.carnum = t.carnum+1
where t.lineid = #{lineid} and t.customerid in (
      SELECT t.customerid from t_storage_logistics_delivery_customer t
      where t.deliveryid = #{deliveryid}
)

UPDATE t_base_logistics_line_customer t
  set t.carnum = t.carnum-1
where t.lineid = #{lineid} and t.customerid in (
      SELECT t.customerid from t_storage_logistics_delivery_customer t
      where t.deliveryid = #{deliveryid}
)
